mapper.select:
select userid from (
select count(cardno) as cardnumber,userid from  Crd_MembershipCard_User a
group by userid) as a where a.cardnumber=1;

mapper.insert:
insert into crd_membershipcard_base
cardno, cardid, cardname, cardtype, isfree, typeid, originalfee, isopen, periodvalidity, times, points, curtimes, totaltimes, totalfee, createdon, createdby, createdname, createdip, lastedon, lastedby, lastedname, lastedip
#{cardno}, #{cardid}, #{cardname}, #{cardtype}, #{isfree}, #{typeid}, #{originalfee} :: FLOAT8, #{isopen} , #{periodvalidity}, #{times}, #{points}, #{curtimes}, #{totaltimes}, #{totalfee} :: FLOAT8, now(), #{createdby}, #{createdname}, #{createdip}, now(), #{lastedby}, #{lastedname}, #{lastedip}
