mapper.select:
select COALESCE(round(t1.attendence::numeric/t2.amount::numeric,4)*100, 0) as TotalAttendance from ( select sum(SignedNumber/ReservableNumber) as attendence from Cur_TeamSchedule where isremoved=false and SignedNumber>=1 and ScheduleDate >= #{begintime}::timestamp and ScheduleDate <= #{endtime}::timestamp
and storeid=#{storeid}::bigint
) as t1, ( select count(*) as amount from Cur_TeamSchedule where isremoved=false and SignedNumber >=1 and ScheduleDate >= #{begintime}::timestamp and ScheduleDate <= #{endtime}::timestamp
and storeid=#{storeid}::bigint
) as t2;

select COALESCE(round(t1.attendence::numeric/t2.amount::numeric,4)*100, 0) as TotalAttendance from ( select sum(SignedNumber/ReservableNumber) as attendence from Cur_TeamSchedule where isremoved=false and SignedNumber >=1 and ScheduleDate >=#{begintime}::timestamp and ScheduleDate <=#{endtime}::timestamp
and storeid=#{storeid}::bigint
and cid=#{cid}::bigint
) as t1, ( select count(*) as amount from Cur_TeamSchedule where isremoved=false and SignedNumber >=1 and ScheduleDate >=#{begintime}::timestamp and ScheduleDate <=#{endtime}::timestamp
and storeid=#{storeid}::bigint
and cid=#{cid}::bigint
) as t2;
