mapper.select:
select userid, storeid::varchar , tel, name, sex, refuserid, saleuserid,
status,createdby, createdname, createdip
,(select roleid from User_Staff b where b.userid=a.userid) as roleid from user_base a where tel=#{tel}

select to_date(createdon::text,'yyyy-MM-dd'),sum(traineenum) from ord_ordercourse where createdon > #{day1}::date and createdon < #{day2}::date+interval '1 day'
and storeId=#{storeid} :: bigint
group by to_date(createdon::text,'yyyy-MM-dd') order by to_date(createdon::text,'yyyy-MM-dd') asc ;
